mapper.select:
SELECT user_Id
FROM review
WHERE review_id = #{reviewId};

mapper.update:
UPDATE review
SET review_viewCount = review_viewCount+1
WHERE review_id = #{reviewId};

UPDATE review
SET review_content = #{reviewContent}, review_writer = #{reviewWriter}, review_title = #{reviewTitle}
WHERE review_id = #{reviewId};

mapper.select:
SELECT id, content, writer, title, view_cnt, date_format(reg_date, '%y-%m-%d') AS reg_date FROM board
WHERE ${key} LIKE concat('%', #{word}, '%')
ORDER BY ${orderBy} ${orderByDir}
